let addHeader=function(e){return`<p class="header">${e}</p><hr class="header">`},addButton=function(e,t){return`<button class="option" onclick="${e}">${t}</button>`};$(function(){displayStats(),displayInventory(),$("#history").append(addHeader("HISTORY")),$("#inspect").append(addHeader("INFO")),playEvent(events["intro-0"])});let itemLink=function(e){return`<button style="color: green" onClick="inspectItem('${e.name}')">${e.name}</button>`};function inspectItem(e){let t=items.error;for(let n=0;n<Object.keys(inventory).length;n++)Object.values(inventory)[n].name===e&&(t=Object.values(inventory)[n]);$("#inspect").empty().append(addHeader("INFO")).append(`${t.name}<br><br>`).append(`${t.desc}`)}const items={berries:{type:"consumable",name:"Berries",weight:2,desc:"A fistful of edible berries that can be eaten to restore some health.",effect:"+7 HP"},"blunt-dagger":{type:"weapon",name:"Blunt Dagger",weight:3,desc:"A small but functional dagger that has been used many times in the past. Perhaps it would be of greater use if sharpened.",damage:6,size:"secondary"},error:{type:"debug",name:"Missing Item",weight:0,desc:"That item could not be accessed. Please report this issue to <a href='https://github.com/thebitspud/age-of-ruin/issues' target='_blank'>the developer.</a>"},"healing-salve":{type:"consumable",name:"Rope",weight:1,desc:"A potent herbal blend that can disinfect and heal lesser wounds in no time.",effect:"+15 HP"},"small-backpack":{type:"equipable",name:"Small Backpack",weight:8,desc:"A small pack that can be worn to increase your inventory weight cap.",section:"back"}};let inventory=[];function displayInventory(){let e=$("#inventory");e.empty().append(addHeader("INVENTORY"));let t=0,n=!1;for(let o=0;o<Object.keys(inventory).length;o++)e.append(`${itemLink(Object.values(inventory)[o])}<br>`),"Small Backpack"===Object.values(inventory)[o].name&&(n=!0),t+=Object.values(inventory)[o].weight;n&&(stats.weight.max=100),stats.weight.now=t,displayStats()}function acquireItem(e){e.weight+stats.weight.now>stats.weight.max||(inventory.push(e),$("#info").append(`Acquired ${itemLink(e)}<br><br>`),displayInventory())}const stats={health:{max:100,min:0,now:100},energy:{max:100,min:0,now:100},weight:{max:25,min:0,now:0},str:{max:20,min:0,now:10},int:{max:20,min:0,now:10},agi:{max:20,min:0,now:10},cha:{max:20,min:0,now:10},primary:"None",secondary:"None"};function displayStats(){$("#stats").empty().append(addHeader("STATS")).append(`Health : ${stats.health.now} / ${stats.health.max}`).append(`<br>Energy : ${stats.energy.now} / ${stats.energy.max}`).append(`<br>Weight : ${stats.weight.now} / ${stats.weight.max}`).append(`<br><br>Primary : ${stats.primary}`).append(`<br>Secondary : ${stats.secondary}`)}let addEventToggle=function(e,t){return addButton(`playEvent(events['${e}'])`,t)},pickUpItemPrompt=function(e,t){return addButton(`pickUpSuccess('${e}', '${t}')`,"Pick Up")+addButton(`discardItem('${e}', '${t}')`,"Discard")};const events={"intro-0":{info:"You wake up and open your eyes.",options:addEventToggle("intro-1","Continue")},"intro-1":{info:"The ground you lie on is cold and uneven, but you can't help but admire the countless stars dotting the night sky above you.",options:addEventToggle("intro-2","Continue")},"intro-2":{info:"You sit up and look around, surveying the unfamiliar landscape.",options:addEventToggle("intro-3","Continue")},"intro-3":{info:"You can barely see the light of dawn off in the distance. Although it is still dark out, you can tell that you are in an unkempt grassy field. You do not know where you are or how you got here.",options:addEventToggle("intro-4","Inspect Area")},"intro-4":{info:"You spot a small dagger lying on the ground.",options:pickUpItemPrompt("intro-5","blunt-dagger")},"intro-5":{info:"There appears to be nothing else of value in the region. Moving out of this area would be a good idea.<br><br>Behind you is a towering forest, but in the opposite direction you spot what appears to be a well used road.",options:addEventToggle("forest-0","To the Forest")+addEventToggle("road-0","To the Road")},"forest-0":{info:"You approach the monumental forest, peering into the dark expanse beneath the canopy.",options:addEventToggle("road-0","To the Road")},"road-0":{info:"",options:addEventToggle("road-0","To the Road")}};function playEvent(e){$("#info").append(e.info+"<br><br>"),$("#options").empty().append(e.options)}function pickUpSuccess(e,t){$("#options").empty().append(addEventToggle(e,"Continue")),acquireItem(items[t])}function discardItem(e,t){$("#info").append(`<span style="color: red">Discarded</span> ${itemLink(items[t])}<br><br>`),$("#options").empty().append(addEventToggle(e,"Continue"))}